require_relative '../../lib/005-longest-palindromic-substring/solution.rb'

describe Solution do
  context 'when input has length <= 1' do
    it 'returns correct result' do
      expect(Solution.new.longest_palindrome('')).to eq('')
      expect(Solution.new.longest_palindrome('a')).to eq('a')
    end
  end

  context 'when input has length > 1' do
    it 'returns correct result for short input' do
      expect(Solution.new.longest_palindrome('ac')).to eq('a')
      expect(Solution.new.longest_palindrome('abb')).to eq('bb')
      expect(Solution.new.longest_palindrome('cbbd')).to eq('bb')
      expect(Solution.new.longest_palindrome('qbabcbad')).to eq('abcba')
    end

    it 'returns correct result for long input' do
      expect(Solution.new.longest_palindrome(''))
        .to eq('wnsnw')
      expect(Solution.new.longest_palindrome(''))
        .to eq('xrcrx')
    end

    it 'returns correct result for long repeated input' do
      input = ''
      expect(Solution.new.longest_palindrome(input)).to eq(input)
    end

    it 'returns correct result for special case' do
      expect(Solution.new.longest_palindrome('aacdefcaa')).to eq('aa')
      expect(Solution.new.longest_palindrome('aacaaefacaa')).to eq('aacaa')
    end
  end
end